        fetch(`http://localhost:3000/pups/${dogId}`, {
          method: 'PATCH',
          headers: {
            'Content-Type': 'application/json'},
            body: JSON.stringify({isGoodDog: !isGoodDog})
        })  .then(res => res.json())


      }
    render(){

        return (
        <div id="dog-info">

            <img src={this.props.dog.image} alt="dog" >
            </img>
            <h2>
                {this.props.dog.name}
            </h2>
            <button onClick={() => this.props.changeDog(this.props.dog.id)} >
