import React from 'react';
import './App.css';
import DogFilter from './DogFilter'
import DogBar from './DogBar'
import DogInfo from './DogInfo'

export default class App extends React.Component {

  state = {
    dogs: [],
    selectedDog: ''
  }

  componentDidMount(){
    fetch('http://localhost:3000/pups')
      .then(res => res.json())
      .then(pups => {
        this.setState({
          dogs: pups
         })
      })
  }

  // componentDidUpdate(){
  //   fetch('http://localhost:3000/pups')
  //     .then(res => res.json())
  //     .then(pups => {
  //       this.setState({
  //         dogs: pups
  //        })
  //     })
  // }



  changeDog = (e, dog) => {
    // if (e.target.innerText == "Bad Dog" && dog.

    let newD = dog.isGoodDog = !dog.isGoodDog
    fetch(`http://localhost:3000/pups/${dog.id}`, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json'},
        body: JSON.stringify({isGoodDog: newD})
    })  .then(res => res.json())
        .then(d => console.log(d))
}


  selectedDog = (dogId) => {

   let selected = this.state.dogs.find(dog => dog.id === dogId )
   this.setState({
     selectedDog: selected
   })

  }


  render() {
  return (

    <div className="App">
      <div id="filter-div">
        <DogFilter /> 
      </div>
      <div>
          <DogBar dogs={this.state.dogs} selected={this.selectedDog} />       
      </div>
         <div id="dog-summary-container">
              <h1>DOGGO:</h1>
              <DogInfo dog={this.state.selectedDog} changeDog={this.changeDog} />
         </div>
       </div> 
  );
  }
}
