import React from 'react'




export default class DogInfo extends React.Component {


    componentDidUpdate(){
       this.renderGood()
    }

    renderGood = () => {
        this.props.dog.isGoodDog ? "Good Dog" : "Bad Dog"
    }

    render() {

        return (
        <div id="dog-info">

            <img src={this.props.dog.image} alt="dog" >
            </img>
            <h2>
                {this.props.dog.name}
            </h2>
            <button onClick={() => this.props.changeDog(this.props.dog)}>
                {}
            </button>
        </div>

        )
    }

}
