import React from 'react'




export default class DogInfo extends React.Component {

    state = {
        isGoodDog: this.props.dog.isGoodDog
    }

    componentDidUpdate(){
        this.props.changeDog()
    }    

    render(){

        return (
        <div id="dog-info">

            <img src={this.props.dog.image} alt="dog" >
            </img>
            <h2>
                {this.props.dog.name}
            </h2>
            <button onClick={() => this.props.changeDog(this.props.dog)}>
                {this.state.isGoodDog ? "Good Dog" : "Bad Dog"}
            </button>
        </div>

        )
    }
}
